import React, { useState, useEffect } from "react";
import { Card, Button } from "react-bootstrap";
import axios from "axios";

const ProjectTemplate = props => {
  const [projectState, setProjectState] = useState({
    project: {
      data: [{ title: "", organization: "", description: "" }]
    }
  });
  useEffect(() => {
    axios
      .get(
        "http://localhost:5000/api/projects/project/" + props.match.params.id
      )
      .then(project => {
        setProjectState({ project });
      })
      .catch(err => {
        console.log(err);
      });
    console.log(projectState.project.data[0]);
  }, []);

  const project = projectState.project.data[0];

  return (
    <Card className="w-75 mx-auto my-4">
      <Card.Header>{project.organization}</Card.Header>
      <Card.Body>
        <Card.Title>{project.title}</Card.Title>
        <Card.Text>{project.description}</Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
      <Card.Footer className="text-muted">2 days ago</Card.Footer>
    </Card>
  );
};

export default ProjectTemplate;
