import React, { useState, useEffect } from "react";
import Hero from "./Hero";
import SearchResults from "./SearchResults";
import axios from "axios";

const Main = () => {
  const [state, setState] = useState({
    projects: {
      data: []
    },
    projectCategories: {
      data: [{ name: "Web Development" }, { name: "Marketing" }]
    }
  });

  const handleSetProjects = category => {
    axios
      .get("http://localhost:5000/api/projects/" + category)
      .then(projects => {
        const newState = { ...state };
        newState.projects = projects;
        setState(newState);
      })
      .catch(err => console.log(err));
    console.log(state);
  };

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/project-categories")
      .then(categories => {
        const newState = { ...state };
        newState.projectCategories = categories;
        setState(newState);
      })
      .catch(err => console.log(err));
  });

  return (
    <div>
      <Hero
        handleSetProjects={handleSetProjects}
        categories={state.projectCategories}
      />
      <SearchResults projects={state.projects} />
    </div>
  );
};

export default Main;
