import React, { useState } from "react";
import { Form, Card, Button } from "react-bootstrap";
import axios from "axios";

const registerUser = e => {
  e.preventDefault();

  const email = document.getElementById("formBasicEmail").value;
  const password = document.getElementById("formBasicPassword").value;
  const passwordConfirmation = document.getElementById(
    "formBasicPasswordConfirm"
  ).value;

  if (password === passwordConfirmation) {
    axios
      .post("http://localhost:5000/api/users/add", {
        username: email,
        password: password
      })
      .then(() => console.log("user created with username: " + email))
      .catch(err => console.log(err));
  } else {
    console.log("passwords need to match. Try Again.");
  }
};

const updateValue = value => {
  console.log(value);
};

const Register = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    passwordConfirmation: ""
  });

  return (
    <Card className="w-50 mx-auto my-5 p-3">
      <Form>
        <Form.Text className="h3 mb-3">Create a New Account</Form.Text>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email Address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter Email"
            value={state.email}
            onChange={e => updateValue(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={state.password}
          />
        </Form.Group>
        <Form.Group controlId="formBasicPasswordConfirm">
          <Form.Label>Re-Enter Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={state.passwordConfirmation}
          />
        </Form.Group>
        <Button variant="primary" type="submit" onClick={e => registerUser(e)}>
          Register
        </Button>
      </Form>
    </Card>
  );
};

export default Register;
