import React, { useState, useEffect } from "react";
import Hero from "./Hero";
import SearchResults from "./SearchResults";
import axios from "axios";

const Main = () => {
  const [categoryState, setCategoryState] = useState({
    categories: {
      data: []
    }
  });

  const [projectState, setProjectState] = useState({
    projects: {
      data: []
    }
  });

  // When a new category of projects is selected, this updates the projects list
  const handleSetProjects = category => {
    axios
      .get("http://localhost:5000/api/projects/" + category)
      .then(projects => {
        const newState = { ...projectState };
        newState.projects = projects;
        setProjectState(newState);
      })
      .catch(err => console.log(err));
  };

  // GET all the project categories
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/project-categories")
      .then(categories => {
        setCategoryState({ categories });
      })
      .catch(err => console.log(err));

    axios
      .get("http://localhost:5000/api/projects")
      .then(projects => {
        setProjectState({ projects });
      })
      .catch(err => console.log(err));
  }, []);

  return (
    <div>
      <div className="divider"></div>
      <Hero
        categories={categoryState.categories}
        handleSetProjects={handleSetProjects}
      />
      <div className="divider"></div>
      <SearchResults projects={projectState.projects} />
    </div>
  );
};

export default Main;
