import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";

const Hero = props => {
  const [state, setState] = useState({
    categories: {
      data: []
    }
  });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/project-categories")
      .then(projectCategories => {
        setState({ categories: projectCategories });
      })
      .catch(err => console.log(err));
  });
  console.log(state.categories.data);

  return (
    <div className="hero">
      <div>
        <h1>Find Your Passion. Sooner.</h1>
        <select className="select-category">
          <option value="default">Select a Category</option>
          {state.categories.data.map(category => (
            <option>{category.name}</option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default Hero;
