import React from "react";
import { Nav, Dropdown, Navbar } from "react-bootstrap";
import cookie from "js-cookie";

const Header = props => {
  const currentUser = props.currentUser.data;
  const handleLogOutUser = () => {
    cookie.remove("crumbl");
    window.open("/", "_self");
  };

  return (
    <Navbar
      bg="light"
      variant="light"
      collapseOnSelect
      expand="lg"
      sticky="top"
    >
      <a className="navbar-brand" href="/">
        <img
          src="https://res.cloudinary.com/justin-bresee/image/upload/v1580358986/Internskip/internskip_logo_x2n2pm.png"
          width="200"
          alt=""
        />
      </a>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav
          className="mr-auto"
          style={{ display: !props.auth ? "inline" : "none" }}
        >
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link href="/about">About</Nav.Link>
          <Nav.Link
            id="login-button"
            href="/login"
            className="bg-success text-light rounded px-3 mx-3"
            style={{ maxWidth: "75px" }}
          >
            Login
          </Nav.Link>
        </Nav>
        <Dropdown
          drop="left"
          style={{ display: props.auth ? "inline" : "none" }}
        >
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            {currentUser.first_name + " " + currentUser.last_name}
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item href="#/action-1">Profile</Dropdown.Item>
            <Dropdown.Item href="#/action-1">Portfolio</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Settings</Dropdown.Item>
            <Dropdown.Item onClick={() => handleLogOutUser()}>
              Sign Out
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default Header;
