import React, { useState } from "react";
import { Form, Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
import cookie from "js-cookie";

const Login = () => {
  const [state, setState] = useState({
    username: "",
    password: ""
  });

  const handleAuthenticateUser = e => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/api/users/auth", {
        username: state.username,
        password: state.password
      })
      .then(user => {
        // cookie.set("crumbl", user.data.token);
        window.open("/");
      })
      .catch(err => console.log(err));
  };

  const handleChangeUsername = e => {
    const newState = { ...state };
    newState.username = e.target.value;
    setState(newState);
  };

  const handleChangePassword = e => {
    const newState = { ...state };
    newState.password = e.target.value;
    setState(newState);
  };

  return (
    <Card className="w-50 mx-auto my-5 p-3">
      <Form>
        <Form.Text className="h3 mb-3">Login</Form.Text>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            onChange={e => handleChangeUsername(e)}
            value={state.username}
            type="email"
            placeholder="Enter email"
          />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            onChange={e => handleChangePassword(e)}
            value={state.password}
            type="password"
            placeholder="Password"
          />
        </Form.Group>
        <Form.Group controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Remember Me" />
        </Form.Group>
        <Button
          variant="primary"
          type="submit"
          onClick={e => handleAuthenticateUser(e)}
        >
          Login
        </Button>
        <br />
        <Link to="/register" className="text-primary">
          Register for new account | &nbsp;
        </Link>
        <Link to="/register" className="text-primary">
          Forgot your Password?
        </Link>
      </Form>
    </Card>
  );
};

export default Login;
