import React, { useState } from "react";
import Hero from "./Hero";
import SearchResults from "./SearchResults";
import axios from "axios";

const Main = props => {
  const [state, setState] = useState({
    projects: {
      data: []
    },
    category: "Marketing"
  });

  const url = "http://localhost:5000/api/projects/" + state.category;
  axios
    .get(url)
    .then(projects => setState({ projects: projects }))
    .catch(err => console.log(err));

  return (
    <div>
      <Hero />
      <SearchResults projects={state.projects} />
    </div>
  );
};

export default Main;
