import React, { useState } from "react";
import Hero from "./Hero";
import SearchResults from "./SearchResults";
import axios from "axios";

const fetchProjectCategories = () => {
  const categories = {
    data: [{ name: "Web dev" }]
  };
  return categories;
};

const Main = props => {
  const [state, setState] = useState({
    projects: {
      data: []
    },
    projectCategories: fetchProjectCategories(),
    category: "Web Development"
  });

  // GET Project Categories
  // axios
  //   .get("http://localhost:5000/api/project-categories")
  //   .then(projectCategories => {
  //     const newState = { ...state };
  //     newState.projectCategories = projectCategories;
  //     setState(newState);
  //   })
  //   .catch(err => console.log(err));

  // GET Projects
  const url = "http://localhost:5000/api/projects/" + state.category;
  axios
    .get(url)
    .then(projects => {
      const newState = { ...state };
      newState.projects = projects;
      setState(newState);
    })
    .catch(err => console.log(err));

  const handleChangeCategory = category => {
    const newState = { ...state };
    newState.category = category;
    setState(newState);
  };

  return (
    <div>
      <Hero
        handleChangeCategory={handleChangeCategory}
        categories={state.projectCategories}
      />
      <SearchResults projects={state.projects} />
    </div>
  );
};

export default Main;
