import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Hero from "./Hero";
import SimpleCard from "./SimpleCard";
import Messages from "./Messages";
import { Row, Col, Jumbotron, Container, Button } from "react-bootstrap";
import axios from "axios";

const Main = () => {
  const [projectCategories, setProjectCategories] = useState({
    data: []
  });
  const [message, setMessage] = useState({
    type: "",
    text: ""
  });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/project-categories/home-page")
      .then(categories => setProjectCategories(categories))
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="bg-light">
      <Messages />
      <div className="divider"></div>
      <Hero />
      <div className="divider"></div>
      <div className="container mx-auto">
        <h5 className="mt-5 mb-3 mx-2">Find a Project Category</h5>
        <Row>
          {projectCategories.data.map((project, index) => (
            <SimpleCard
              name={project.name}
              img={project.imageURL}
              index={index}
              key={index}
            />
          ))}
        </Row>
        <p className="text-muted mt-5 pb-5 text-center">
          Don't see what you want? Click <Link to="/">here</Link> for more
          categories!
        </p>
      </div>
      <Jumbotron
        fluid
        id="how-it-works"
        className="text-white mb-0"
        style={{ backgroundColor: "#2d6db5" }}
      >
        <Container>
          <h1>How it Works</h1>
          <p>This section describes how the process works to the clients.</p>
          <Row className="text-dark">
            <SimpleCard name="Companies post projects." />
            <SimpleCard name="Students select projects to work on." />
            <SimpleCard name="Instructors assist students on projects." />
            <SimpleCard name="All parties benefit!" />
          </Row>
        </Container>
      </Jumbotron>
      <Jumbotron className="mb-0">
        <Row>
          <Col style={{ textShadow: "3px 5px 8px #aaa" }}>
            <h1>Build A Portflio With Real Companies</h1>
            <p>
              Internskip connects businesses with students so that students get
              the real-world experience that companies want to hire.
            </p>
            <p>
              <Button variant="primary">Get Started</Button>
            </p>
          </Col>
          <Col>
            <div className="woman-image shadow"></div>
          </Col>
        </Row>
      </Jumbotron>
    </div>
  );
};

export default Main;
