import React, { useState } from "react";
import {
  ProgressBar,
  Button,
  Card,
  InputGroup,
  FormControl,
  Form
} from "react-bootstrap";

const PostProject = () => {
  // sets values of properties
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [visibility, setVisibility] = useState("");

  // sets display of all the slides
  const [slideDisplays, setSlideDisplays] = useState([
    { display: true },
    { display: false },
    { display: false },
    { display: false }
  ]);

  const handleSetSlideDisplays = (hide, show) => {
    console.log(slideDisplays);
  };

  return (
    <div>
      <Card
        style={{ display: slideDisplays.slideOne ? "" : "none" }}
        className="w-75 my-4 mx-auto p-5"
      >
        <ProgressBar now={25} className="mb-3" />
        <Form>
          <h3>Basic Information</h3>
          <p className="text-muted">Tell us about your project.</p>
          <InputGroup className="mb-3">
            <InputGroup.Prepend>
              <InputGroup.Text id="basic-addon1">Title</InputGroup.Text>
            </InputGroup.Prepend>
            <FormControl
              value={title}
              onChange={e => setTitle(e.target.value)}
              placeholder="Example: 'Marketing Strategy'"
              aria-label="title"
              aria-describedby="basic-addon1"
            />
          </InputGroup>
          <InputGroup className="mb-3" style={{ height: "150px" }}>
            <InputGroup.Prepend>
              <InputGroup.Text id="basic-addon1">Description</InputGroup.Text>
            </InputGroup.Prepend>
            <FormControl
              value={description}
              onChange={e => setDescription(e.target.value)}
              placeholder="Enter a project description..."
              as="textarea"
              aria-label="description"
            />
          </InputGroup>
        </Form>
        <Button variant="primary" onClick={() => handleSetSlideDisplays()}>
          Next
        </Button>
      </Card>

      <Card
        style={{ display: slideDisplays.slideTwo ? "" : "none" }}
        className="w-75 my-4 mx-auto p-5"
      >
        <ProgressBar now={50} className="mb-3" />
        <h3>Upload Project Resources</h3>
        <p className="text-muted">
          Choose any files, images, or assets to include in project.
        </p>
      </Card>
    </div>
  );
};

export default PostProject;
