import React, { useState } from "react";
import {
  ProgressBar,
  Button,
  Card,
  InputGroup,
  FormControl,
  Form
} from "react-bootstrap";

const PostProject = () => {
  const [progress, setProgress] = useState(25);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [visibility, setVisibility] = useState("");

  // sets display of all the slides
  const [slideOneDisplay, setSlideOneDisplay] = useState(true);

  return (
    <Card
      style={{ display: slideOneDisplay ? "" : "none" }}
      className="w-75 my-4 mx-auto p-5"
    >
      <ProgressBar now={progress} />
      <Form>
        <h3>Basic Information</h3>
        <p className="text-muted">Tell us about your project.</p>
        <InputGroup className="mb-3">
          <InputGroup.Prepend>
            <InputGroup.Text id="basic-addon1">Title</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl
            value={title}
            onChange={e => setTitle(e.target.value)}
            placeholder="Username"
            aria-label="title"
            aria-describedby="basic-addon1"
          />
        </InputGroup>
        <InputGroup className="mb-3" style={{ height: "150px" }}>
          <InputGroup.Prepend>
            <InputGroup.Text id="basic-addon1">Description</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl
            value={description}
            onChange={e => setDescription(e.target.value)}
            placeholder="Username"
            as="textarea"
            aria-label="description"
          />
        </InputGroup>
      </Form>
    </Card>
  );
};

export default PostProject;
