import React from "react";
import { Form, Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Redirect } from "react-router-dom";
import axios from "axios";

const authenticateUser = e => {
  e.preventDefault();
  const email = document.getElementById("formBasicEmail").value;
  const password = document.getElementById("formBasicPassword").value;
  const url =
    "http://localhost:5000/api/users/authenticate/" + email + "/" + password;

  axios
    .get(url)
    .then(user => <Redirect to="/home" />)
    .catch(err => console.log(err));
};

const Login = () => {
  return (
    <Card className="w-50 mx-auto my-5 p-3">
      <Form>
        <Form.Text className="h3 mb-3">Login</Form.Text>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>
        <Form.Group controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Remember Me" />
        </Form.Group>
        <Button
          variant="primary"
          type="submit"
          onClick={e => authenticateUser(e)}
        >
          Login
        </Button>
        <Link to="/register" className="text-primary">
          <p>Register for new account.</p>
        </Link>
      </Form>
    </Card>
  );
};

export default Login;
