import React, { useState } from "react";
import {
  ProgressBar,
  Button,
  Card,
  InputGroup,
  FormControl
} from "react-bootstrap";

const PostProject = () => {
  const slideOne = (
    <Card className="w-75 mx-auto my-5 p-4">
      <ProgressBar now={25} className="mb-4" />
      <InputGroup className="mb-3">
        <InputGroup.Prepend>
          <InputGroup.Text id="basic-addon1">Project Title</InputGroup.Text>
        </InputGroup.Prepend>
        <FormControl
          placeholder="example: 'Marketing Strategy'"
          aria-label="Title"
          aria-describedby="basic-addon1"
        />
      </InputGroup>

      <InputGroup className="my-4" style={{ height: "200px" }}>
        <InputGroup.Prepend>
          <InputGroup.Text>Description</InputGroup.Text>
        </InputGroup.Prepend>
        <FormControl
          as="textarea"
          aria-label="With textarea"
          placeholder="Write a short description about your project..."
        />
      </InputGroup>
      <div className="text-right">
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideTwo);
          }}
        >
          Next
        </Button>
      </div>
    </Card>
  );

  const slideTwo = (
    <Card className="w-75 mx-auto my-5 p-4">
      <ProgressBar now={50} className="mb-4" />
      <h3>Upload Project Resources</h3>
      <div className="text-right">
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideOne);
          }}
        >
          Previous
        </Button>
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideThree);
          }}
        >
          Next
        </Button>
      </div>
    </Card>
  );
  const slideThree = (
    <Card className="w-75 mx-auto my-5 p-4">
      <ProgressBar now={50} className="mb-4" />
      <h3>Set Visibility</h3>
      <p className="text-muted">Choose who has access to your project.</p>
      <div className="text-right">
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideTwo);
          }}
        >
          Previous
        </Button>
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideFour);
          }}
        >
          Next
        </Button>
      </div>
    </Card>
  );
  const slideFour = <Card className="w-75 mx-auto my-5 p-4"></Card>;

  const [currentSlide, setCurrentSlide] = useState(slideOne);

  return <div>{currentSlide}</div>;
};

export default PostProject;
