import React, { useState } from "react";
import axios from "axios";

const Hero = props => {
  const [state, setState] = useState({
    categories: {
      data: []
    }
  });

  const setCategories = () => {
    axios
      .get("http://localhost:5000/api/project-categories")
      .then(categories => {
        setState({ categories });
      })
      .catch(err => console.log(err));
  };
  return (
    <div className="hero">
      <div>
        <h1>Find Your Passion. Sooner.</h1>
        <select className="select-category" onLoad={setCategories()}>
          <option value="default">Select a Category</option>
          {state.categories.data.map(category => (
            <option value={category.name} key={category._id}>
              {category.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default Hero;
