import React, { useState } from "react";
import {
  Form,
  ProgressBar,
  Button,
  Card,
  InputGroup,
  FormControl
} from "react-bootstrap";

const PostProject = () => {
  const [progress, setProgress] = useState(25);

  const slideOne = (
    <Card className="w-75 mx-auto my-5 p-4">
      <Form>
        <ProgressBar now={progress} className="mb-4" />
        <InputGroup className="mb-3">
          <InputGroup.Prepend>
            <InputGroup.Text id="basic-addon1">Project Title</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl
            placeholder="example: 'Marketing Strategy'"
            aria-label="Title"
            aria-describedby="basic-addon1"
          />
        </InputGroup>

        <InputGroup className="my-4" style={{ height: "200px" }}>
          <InputGroup.Prepend>
            <InputGroup.Text>Description</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl
            as="textarea"
            aria-label="With textarea"
            placeholder="Write a short description about your project..."
          />
        </InputGroup>
        <div className="text-right">
          <Button
            variant="primary"
            style={{ width: "100px" }}
            onClick={() => {
              setCurrentSlide(slideTwo);
              setProgress(50);
            }}
          >
            Next
          </Button>
        </div>
      </Form>
    </Card>
  );
  const slideTwo = <Card className="w-75 mx-auto my-5 p-4"></Card>;
  const slideThree = <Card className="w-75 mx-auto my-5 p-4"></Card>;
  const slideFour = <Card className="w-75 mx-auto my-5 p-4"></Card>;

  const [currentSlide, setCurrentSlide] = useState(slideOne);

  return <div>{currentSlide}</div>;
};

export default PostProject;
