import React, { useState } from "react";
import { Form, Card, Button } from "react-bootstrap";
import axios from "axios";

const Register = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    passwordConfirmation: ""
  });

  const registerUser = e => {
    e.preventDefault();

    if (state.password === state.passwordConfirmation) {
      axios
        .post("http://localhost:5000/api/users/add", {
          username: state.email,
          password: state.password
        })
        .then(() => console.log("user created with username: " + state.email))
        .catch(err => console.log(err));
    } else {
      console.log("passwords need to match. Try Again.");
    }
  };

  const updateValues = (value, type) => {
    if (type === "email") {
      const newState = { ...state };
      newState.email = value;
      setState(newState);
    } else if (type === "password") {
      const newState = { ...state };
      newState.password = value;
      setState(newState);
    } else {
      const newState = { ...state };
      newState.passwordConfirmation = value;
      setState(newState);
    }
  };

  return (
    <Card className="w-50 mx-auto my-5 p-3">
      <Form>
        <Form.Text className="h3 mb-3">Create a New Account</Form.Text>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email Address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter Email"
            value={state.email}
            onChange={e => updateValues(e.target.value, "email")}
          />
        </Form.Group>
        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={state.password}
            onChange={e => updateValues(e.target.value, "password")}
          />
        </Form.Group>
        <Form.Group controlId="formBasicPasswordConfirm">
          <Form.Label>Re-Enter Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={state.passwordConfirmation}
            onChange={e => updateValues(e.target.value, "passwordConfirmation")}
          />
        </Form.Group>
        <Button variant="primary" type="submit" onClick={e => registerUser(e)}>
          Register
        </Button>
      </Form>
    </Card>
  );
};

export default Register;
