import React from "react";
import { Form, Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
import cookie from "js-cookie";

const Login = () => {
  const handleAuthenticateUser = e => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/api/users/auth", {
        username: state.username,
        password: state.password
      })
      .then(user => {
        cookie.set("intern_s_c", user.token);
      })
      .catch(err => console.log(err));
  };
  return (
    <Card className="w-50 mx-auto my-5 p-3">
      <Form>
        <Form.Text className="h3 mb-3">Login</Form.Text>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>
        <Form.Group controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Remember Me" />
        </Form.Group>
        <Button
          variant="primary"
          type="submit"
          onClick={e => handleAuthenticateUser(e)}
        >
          Login
        </Button>
        <br />
        <Link to="/register" className="text-primary">
          Register for new account | &nbsp;
        </Link>
        <Link to="/register" className="text-primary">
          Forgot your Password?
        </Link>
      </Form>
    </Card>
  );
};

export default Login;
