import React, { useState, useEffect } from "react";
import Hero from "./Hero";
import SearchResults from "./SearchResults";
import axios from "axios";

const Main = () => {
  const [projectCategories, setProjectCategories] = useState({
    projectCategories: {
      data: []
    }
  });

  useEffect(async () => {
    const result = await axios("http://localhost:5000/api/project-categories");
    setProjectCategories(result.data);
  }, []);
  return (
    <div>
      <Hero
        handleSetProjects={handleSetProjects}
        categories={state.projectCategories}
      />
      <SearchResults projects={state.projects} />
    </div>
  );
};

export default Main;
