import React, { useState } from "react";
import SearchResult from "./SearchResult";
import axios from "axios";

const SearchResults = props => {
  const [state, setState] = useState({
    projects: {
      data: []
    }
  });
  const setProjectsInitial = () => {
    axios
      .get("http://localhost:5000/api/projects/" + props.category)
      .then(projects => {
        setState({ projects });
      })
      .catch(err => console.log(err));
  };
  return (
    <div className="row d-flex justify-content-center mx-auto my-4 w-75">
      {state.projects.data.length === 0 ? "" : setProjectsInitial()}
      {state.projects.data.map(project => (
        <SearchResult project={project} key={project._id} />
      ))}
    </div>
  );
};

export default SearchResults;
