import React from "react";
import { Toast } from "react-bootstrap";

const Message = props => {
  return (
    <Toast
      className="mx-3"
      style={{ display: props.message.display ? "" : "none" }}
    >
      <Toast.Header>
        <strong className="mr-auto">{props.message.type}</strong>
        <small>just now</small>
      </Toast.Header>
      <Toast.Body>{props.message.text}</Toast.Body>
    </Toast>
  );
};

export default Message;
