import React, { useState } from "react";
import {
  ProgressBar,
  Button,
  Card,
  InputGroup,
  FormControl,
  Form
} from "react-bootstrap";

const PostProject = () => {
  const [title, setTitle] = useState("example: 'Marketing Strategy'");
  const [description, setDescription] = useState("");
  const [visibility, setVisibility] = useState("");

  const handleSubmitProject = () => {
    console.log("project created");
  };
  const slideOne = (
    <Card className="w-75 mx-auto my-5 p-4">
      <Form>
        <ProgressBar now={25} className="mb-4" />
        <h3>Basic Information</h3>
        <p className="text-muted">Tell us about your project.</p>
        <Form.Text className="h3 mb-3">Project Title</Form.Text>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            onChange={e => setTitle(e.target.value)}
            value={title}
            placeholder="Example: 'Marketing Strategy'"
          />
        </Form.Group>
        <InputGroup className="my-4" style={{ height: "200px" }}>
          <InputGroup.Prepend>
            <InputGroup.Text>Description</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl
            as="textarea"
            aria-label="With textarea"
            placeholder="Write a short description about your project..."
          />
        </InputGroup>
        <div className="text-right">
          <Button
            className="mx-2"
            variant="primary"
            style={{ width: "100px" }}
            onClick={() => {
              setCurrentSlide(slideTwo);
            }}
          >
            Next
          </Button>
        </div>
      </Form>
    </Card>
  );

  const slideTwo = (
    <Card className="w-75 mx-auto my-5 p-4">
      <ProgressBar now={50} className="mb-4" />
      <h3>Upload Project Resources</h3>
      <p className="text-muted">
        Add files, images, or other assets to project.
      </p>
      <div className="text-right">
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideOne);
          }}
        >
          Previous
        </Button>
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideThree);
          }}
        >
          Next
        </Button>
      </div>
    </Card>
  );
  const slideThree = (
    <Card className="w-75 mx-auto my-5 p-4">
      <ProgressBar now={75} className="mb-4" />
      <h3>Set Visibility</h3>
      <p className="text-muted">Choose who has access to your project.</p>
      <div className="text-right">
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideTwo);
          }}
        >
          Previous
        </Button>
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideFour);
          }}
        >
          Next
        </Button>
      </div>
    </Card>
  );
  const slideFour = (
    <Card className="w-75 mx-auto my-5 p-4">
      <ProgressBar now={100} className="mb-4" />
      <h3>Review & Submit</h3>
      <p className="text-muted">Make sure all the information is correct.</p>
      <div className="text-right">
        <Button
          className="mx-2"
          variant="primary"
          style={{ width: "100px" }}
          onClick={() => {
            setCurrentSlide(slideThree);
          }}
        >
          Previous
        </Button>
        <Button
          className="mx-2"
          variant="success"
          style={{ width: "150px" }}
          onClick={() => {
            handleSubmitProject();
          }}
        >
          Create Project
        </Button>
      </div>
    </Card>
  );

  const [currentSlide, setCurrentSlide] = useState(slideOne);

  return <div>{currentSlide}</div>;
};

export default PostProject;
