import React from "react";
import { Nav, Form, Dropdown, Navbar } from "react-bootstrap";
import cookie from "js-cookie";

const Header = props => {
  const currentUser = props.currentUser.data;
  const handleLogOutUser = () => {
    cookie.remove("crumbl");
    window.open("/", "_self");
  };

  return (
    <Navbar
      bg="light"
      variant="light"
      collapseOnSelect
      expand="lg"
      sticky="top"
    >
      <a className="navbar-brand" href="/">
        <img src="images/logo.png" width="200" alt="" />
      </a>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link href="/about">About</Nav.Link>
          <Nav.Link
            href="/login"
            className="bg-success text-light rounded px-3 mx-3"
            style={{ maxWidth: "75px" }}
          >
            Login
          </Nav.Link>
        </Nav>
        <Form inline>
          <Dropdown drop="left">
            <Dropdown.Toggle variant="success" id="dropdown-basic">
              {currentUser.first_name + " " + currentUser.last_name}
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">Profile</Dropdown.Item>
              <Dropdown.Item href="#/action-1">Portfolio</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Settings</Dropdown.Item>
              <Dropdown.Item onClick={() => handleLogOutUser()}>
                Sign Out
              </Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </Form>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default Header;
